   236	const categories = ['Tous', 'Bien-être', 'Cosmétiques', 'Accessoires', 'Épices'];
   237	
   238	function NinaStoreContent() {
   239	  const [searchTerm, setSearchTerm] = useState('');
   240	  const [selectedCategory, setSelectedCategory] = useState('Tous');
   241	  const [favorites, setFavorites] = useState<number[]>([]);
   242	  const { addToCart } = useCart();
   243	
   244	  const filteredProducts = products.filter(product => {
   245	    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase());
   246	    const matchesCategory = selectedCategory === 'Tous' || product.category === selectedCategory;
   247	    return matchesSearch && matchesCategory;
   248	  });
   249	
   250	  const toggleFavorite = (productId: number) => {
   251	    setFavorites(prev => 
   252	      prev.includes(productId) 
   253	        ? prev.filter(id => id !== productId)
   254	        : [...prev, productId]
   255	    );
   256	  };
   257	
   258	  return (
   259	    <div className="min-h-screen bg-gradient-to-br from-white via-gray-50 to-white">
   260	      <HeaderWithCart />
   261	      <Cart />
   262	
   263	      {/* Hero Section */}
   264	      <section className="relative h-[60vh] overflow-hidden mt-20">
   265	        <div className="absolute inset-0">
   266	
   267	          <div className="absolute inset-0 nina-luxury-overlay" />
   268	        </div>
   269	        
   270	        <div className="relative z-10 h-full flex items-center justify-center">
   271	          <div className="text-center text-white max-w-4xl mx-auto px-4">
   272	            <h1 className="text-5xl md:text-7xl font-playfair font-light mb-6 luxury-text-shadow">
   273	              Nina Store
   274	            </h1>
   275	            <p className="text-xl md:text-2xl mb-8 opacity-90">
   276	              Votre boutique de produits de beauté et bien-être
   277	            </p>
   278	            <p className="text-lg mb-8 max-w-2xl mx-auto leading-relaxed opacity-80">
   279	              Découvrez notre sélection exclusive de cosmétiques, produits de bien-être, 
   280	              accessoires et épices authentiques.
   281	            </p>
   282	          </div>
   283	        </div>
   284	      </section>
   285	
   286	      {/* Search and Filter Section */}
   287	      <section className="py-8 bg-white/80 backdrop-blur-sm sticky top-20 z-40 border-b border-gray-200">
   288	        <div className="container mx-auto px-4">
   289	          <div className="flex flex-col md:flex-row gap-4 items-center justify-between">
   290	            {/* Search */}
   291	            <div className="relative flex-1 max-w-md">
   292	              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
   293	              <Input
   294	                type="text"
   295	                placeholder="Rechercher un produit..."
   296	                value={searchTerm}
   297	                onChange={(e) => setSearchTerm(e.target.value)}
   298	                className="pl-10 border-gray-300 focus:border-[var(--nina-burgundy)] focus:ring-[var(--nina-burgundy)]"
   299	              />
   300	            </div>
   301	
   302	            {/* Categories */}
   303	            <div className="flex flex-wrap gap-2">
   304	              {categories.map((category) => (
   305	                <Button
   306	                  key={category}
   307	                  variant={selectedCategory === category ? "default" : "outline"}
   308	                  onClick={() => setSelectedCategory(category)}
   309	                  className={`${
   310	                    selectedCategory === category 
   311	                      ? 'nina-luxury-gradient-dark text-white' 
   312	                      : 'border-gray-300 hover:border-[var(--nina-burgundy)] hover:text-[var(--nina-burgundy)]'
   313	                  } transition-all duration-300`}
   314	                >
   315	                  {category}
   316	                </Button>
   317	              ))}
   318	            </div>
   319	          </div>
   320	        </div>
   321	      </section>
   322	
   323	      {/* Products Grid */}
   324	      <section className="py-16">
   325	        <div className="container mx-auto px-4">
   326	          <div className="mb-8">
   327	            <h2 className="text-3xl font-playfair nina-burgundy mb-4">
   328	              {selectedCategory === 'Tous' ? 'Tous nos produits' : selectedCategory}
   329	            </h2>
   330	            <p className="text-gray-600">
   331	              {filteredProducts.length} produit{filteredProducts.length > 1 ? 's' : ''} trouvé{filteredProducts.length > 1 ? 's' : ''}
   332	            </p>
   333	          </div>
   334	
   335	          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
   336	            {filteredProducts.map((product) => (
   337	              <Card key={product.id} className="group hover-lift border-0 luxury-shadow overflow-hidden animate-slide-up luxury-glow bg-white/80 backdrop-blur-sm">
