   357	                      className={`p-3 rounded-full backdrop-blur-md transition-all duration-300 hover:scale-110 luxury-shadow ${
   358	                        favorites.includes(product.id)
   359	                          ? 'bg-red-500 text-white shadow-red-500/25'
   360	                          : 'bg-white/90 text-gray-600 hover:bg-red-500 hover:text-white'
   361	                      }`}
   362	                    >
   363	                      <Heart size={18} fill={favorites.includes(product.id) ? 'currentColor' : 'none'} />
   364	                    </button>
   365	                    <button className="p-3 rounded-full bg-white/90 text-gray-600 hover:bg-[var(--nina-burgundy)] hover:text-white backdrop-blur-md transition-all duration-300 hover:scale-110 luxury-shadow">
   366	                      <Eye size={18} />
   367	                    </button>
   368	                    <button
   369	                      onClick={() => addToCart({
   370	                        id: product.id,
   371	                        name: product.name,
   372	                        price: product.price,
   373	                        image: product.image,
   374	                        category: product.category
   375	                      })}
   376	                      className="p-3 rounded-full bg-[var(--nina-burgundy)] text-white hover:bg-[var(--nina-gold)] backdrop-blur-md transition-all duration-300 hover:scale-110 luxury-shadow"
