     9	  const [isMenuOpen, setIsMenuOpen] = useState(false);
    10	  const [isScrolled, setIsScrolled] = useState(false);
    11	  const [activeSection, setActiveSection] = useState('accueil');
    12	
    13	  const toggleMenu = () => setIsMenuOpen(!isMenuOpen);
    14	
    15	  const scrollToSection = (sectionId: string) => {
    16	    // Si on n'est pas sur la page d'accueil, rediriger vers la page d'accueil avec l'ancre
    17	    if (window.location.pathname !== '/') {
    18	      window.location.href = `/#${sectionId}`;
