    35	        const sections = ['accueil', 'massage', 'store', 'contact'];
    36	        const scrollPosition = window.scrollY + 100;
    37	
    38	        for (const section of sections) {
    39	          const element = document.getElementById(section);
    40	          if (element) {
    41	            const { offsetTop, offsetHeight } = element;
    42	            if (scrollPosition >= offsetTop && scrollPosition < offsetTop + offsetHeight) {
    43	              setActiveSection(section);
    44	              break;
    45	            }
    46	          }
    47	        }
    48	      } else {
    49	        // Set active section based on current path
    50	        const path = window.location.pathname;
    51	        if (path.includes('nina-store')) {
    52	          setActiveSection('nina-store');
    53	        } else if (path.includes('massage')) {
    54	          setActiveSection('massage');
    55	        } else if (path.includes('contact')) {
    56	          setActiveSection('contact');
    57	        } else {
    58	          setActiveSection('accueil');
    59	        }
    60	      }
    61	    };
    62	
    63	    // Initial call
    64	    handleScroll();
    65	
